
const path = require('path');
const fs = require('fs');

const cwd = process.cwd();
const pkg = require(path.resolve(cwd, 'package.json'));
const versionSource = [
  '',
  '// this file is generated by `npm run build:version`',
  'const version = \'' + pkg.version + '\';',
  'export default version;',
  '',
].join('\n');

fs.writeFileSync(path.resolve(cwd, 'src/version.js'), versionSource, {encoding: 'utf8'});
